mapper.select:
select *
from sys_user
where username = #{username} and role =#{role}

select hashedPassword
from sys_user
where username = #{username}

mapper.update:
update sys_user
set sex=#{sex}, email=#{email}, update_at=#{updateAt}
where username = #{username} limit 1

update sys_user
set hashedPassword=#{password}
where username = #{username} limit 1
